import React, { useState } from "react";
import { Box, Text, useInput } from "ink";

export const SelectableList = ({ items }: { items: string[] }) => {
  const [selectedIndex, setSelectedIndex] = useState(0);

  useInput((input, key) => {
    if (key.upArrow) setSelectedIndex((prev) => Math.max(0, prev - 1));
    if (key.downArrow)
      setSelectedIndex((prev) => Math.min(items.length - 1, prev + 1));
  });

  return (
    <Box flexDirection="column">
      {items.map((item, index) => (
        <Text key={item} color={index === selectedIndex ? "cyan" : "white"}>
          {index === selectedIndex ? "❯ " : "  "}
          {item}
        </Text>
      ))}
    </Box>
  );
};

import React, { useEffect, useState } from "react";
import { Box, Text, useStdout } from "ink";

export const Shell = ({
  children,
  title,
}: {
  children: React.ReactNode;
  title: string;
}) => {
  const { stdout } = useStdout();
  const [size, setSize] = useState({
    columns: stdout?.columns || 80,
    rows: stdout?.rows || 24,
  });

  useEffect(() => {
    if (!stdout) return;

    const onResize = () => {
      setSize({
        columns: stdout.columns,
        rows: stdout.rows,
      });
    };

    stdout.on("resize", onResize);
    return () => {
      stdout.off("resize", onResize);
    };
  }, [stdout]);

  return (
    <Box flexDirection="column" width={size.columns} height={size.rows}>
      {/* HEADER */}
      <Box backgroundColor="blue" paddingX={1} justifyContent="space-between">
        <Text bold color="white">
          {title.toUpperCase()}
        </Text>
        <Text color="cyan">{new Date().toLocaleTimeString()}</Text>
      </Box>

      {/* MAIN CONTENT AREA */}
      <Box flexGrow={1} padding={1} flexDirection="column">
        {children}
      </Box>

      {/* FOOTER / KEYBINDINGS */}
      <Box
        borderStyle="single"
        borderTop={true}
        borderBottom={false}
        borderLeft={false}
        borderRight={false}
        paddingX={1}
      >
        <Text dimColor>q Quit • tab Switch • ↑↓ Navigate</Text>
      </Box>
    </Box>
  );
};

import React from "react";
import { Text, Box } from "ink";

export const Banner = ({
  text,
  font = "Standard",
  color = "white",
}: {
  text: string;
  font?: any;
  color?: string;
}) => {
  const [bannerText, setBannerText] = React.useState(text);

  React.useEffect(() => {
    const loadFiglet = async () => {
      try {
        const figlet = await import("figlet");
        // @ts-ignore
        setBannerText(figlet.default.textSync(text, { font }));
      } catch (e) {
        setBannerText(text);
      }
    };
    loadFiglet();
  }, [text, font]);

  return (
    <Box paddingY={1}>
      <Text color={color}>{bannerText}</Text>
    </Box>
  );
};
